import React, { Component } from 'react';
import './App.css';
import Web3 from 'web3'
import Color from '../abis/Color.json'
import { create } from 'ipfs-http-client';

class App extends Component {

  ipfs;

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if(window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }

    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }

    else {
      window.alert('¡Considera usar Metamask!')
    }

  }

  async loadBlockchainData(){
    const web3 = window.web3
    // Cargar una cuenta
    const accounts = await web3.eth.getAccounts()
    this.setState({account: accounts[0]})
    const networkId = '5777'
    const networkData = Color.networks[networkId]
    if(networkData) {
      const abi = Color.abi 
      const address = networkData.address
      const contract = new web3.eth.Contract(abi, address)
      this.setState({contract})
      // Función 'totalSupply' del Smart Contract
      const totalSupply = await contract.methods.totalSupply().call()
      this.setState({totalSupply})
      // Carga de colores
      for (var i = 1; i<=totalSupply; i++){
        const color = await contract.methods.colors(i-1).call()
        this.setState({colors: [...this.state.colors, color] 
        })
      }
    } else {
      window.alert('¡Smart Contract no desplegado en la red!')
    }
  }

  // Constructor
  constructor(props) {
    super(props)
    this.state = {
      account: '',
      contract: null,
      totalSupply: 0,
      colors: []
    }
    this.ipfs = create({host: 'ipfs.infura.io', port: '5001', protocol: 'http'})
  }

  // Función para un nuevo Color como NFT
  mint = (color) => {
    console.log('¡Nuevo NFT en procedimiento!')
    this.state.contract.methods.mint(color).send({ from: this.state.account })
    .once('receipt', (receipt) => {
      this.setState({
        colors: [...this.state.colors, color]
      })
    })
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="https://frogames.es/rutas-de-aprendizaje"
            target="_blank"
            rel="noopener noreferrer"
          >
            DApp
          </a>

          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small className="text-white">
                <span id="account">{this.state.account}</span>   
              </small>
            </li>
          </ul>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">

                <h1> DApp de un coleccionable de NFT's</h1>
                <form onSubmit={(event) => {
                  event.preventDefault()
                  const color = this.color.value
                  this.mint(color)
                }}>

                <input 
                type = 'text'
                className = 'form-control mb-1'
                placeholder = 'Ej: #FFFFFF'
                ref = {(input) => {this.color = input}}
                />

                <input 
                type = 'submit'
                className="btn btn-block btn-success"
                value = "NUEVO NFT"
                />
                </form>
              </div>
            </main>
          </div>
          <hr/>
          <div className="row text-center">
            {this.state.colors.map((color,key) => {
              return (
                <div key={key} className="col-md-3 mb-3">
                <div className="token" 
                style={{ backgroundColor: color }}></div>
                      <div>{color}</div>
                   </div>
                )
            })}
            </div>
          </div>
        </div>
    );
  }
}

export default App;
